INSERT INTO armor VALUES ( 1, 'Padded', 'Padded armor consists of quilted layers of cloth and batting.', 5, 8, 11, 0, 0, 0 );
INSERT INTO armor VALUES ( 2, 'Leather', 'The breastplate and shoulder protectors of this armor are made of leather that has been stiffened by being boiled in oil. The rest of the armor is made of softer and more flexible materials.', 10, 10, 11, 0, 0, 0 );
INSERT INTO armor VALUES ( 3, 'Studded leather', 'Made from tough but flexible leather, studded leather is reinforced with close-set rivets or spikes.', 45, 13, 12, 0, 0, 0 );
INSERT INTO armor VALUES ( 4, 'Hide', 'This crude armor consists of thick furs and pelts. It is commonly worn by barbarian tribes, evil humanoids, and other folk who lack access to the tools and materials needed to create better armor.', 10, 12, 12, 0, 1, 0 );
INSERT INTO armor VALUES ( 5, 'Chain shirt', 'Made of interlocking metal rings, a chain shirt is worn between layers of clothing or leather. This armor offers modest protection to the wearer''s upper body and allows the sound of the rings rubbing against one another to be muffled by outer layers.', 50, 20, 13, 0, 1, 0 );
INSERT INTO armor VALUES ( 6, 'Scale mail', 'This armor consists of a coat and leggings (and perhaps a separate skirt) of leather covered with overlapping pieces of metal, much like the scales of a fish. The suit includes gauntlets.', 50, 45, 14, 0, 1, 0 );
INSERT INTO armor VALUES ( 7, 'Breastplate', 'This armor consists of a fitted metal chest piece worn with supple leather. Although it leaves the legs and arms relatively unprotected, this armor provides good protection for the wearer''s vital organs while leaving the wearer relatively unencumbered.', 400, 20, 14, 0, 1, 0 );
INSERT INTO armor VALUES ( 8, 'Half plate', 'Half plate consists of shaped metal plates that cover most of the wearer''s body. It does not include leg protection beyond simple greaves that are attached with leather straps.', 750, 40, 15, 0, 1, 0 );
INSERT INTO armor VALUES ( 9, 'Ring mail', 'This armor is leather armor with heavy rings sewn into it. The rings help reinforce the armor against blows from swords and axes. Ring mail is inferior to chain mail, and it''s usually worn only by those who can''t afford better armor.', 30, 40, 14, 0, 2, 0 );
INSERT INTO armor VALUES ( 10, 'Chain mail', 'Made of interlocking metal rings, chain mail includes a layer of quilted fabric worn underneath the mail to prevent chafing and to cushion the impact of blows. The suit includes gauntlets.', 75, 55, 16, 0, 2, 0 );
INSERT INTO armor VALUES ( 11, 'Splint', 'This armor is made of narrow vertical strips of metal riveted to a backing of leather that is worn over cloth padding. Flexible chain mail protects the joints.', 200, 60, 17, 0, 2, 0 );
INSERT INTO armor VALUES ( 12, 'Plate', 'Plate consists of shaped, interlocking metal plates to cover the entire body. A suit of plate includes gauntlets, heavy leather boots, a visored helmet, and thick layers of padding underneath the armor. Buckles and straps distribute the weight over the body.', 1500, 65, 18, 0, 2, 0 );
INSERT INTO armor VALUES ( 13, 'Shield', 'A shield is made from wood or metal and is carried in one hand. Wielding a shield increases your Armor Class by 2. You can benefit from only one shield at a time.', 10, 6, 2, 0, 3, 0 );
