UPDATE ability_type SET id = 3 WHERE ability_type = 2;
UPDATE ability_type SET id = 2 WHERE ability_type = 1;
UPDATE ability_type SET id = 1 WHERE ability_type = 0;

UPDATE ability SET ability_type_id = 3 WHERE ability_type_id = 2;
UPDATE ability SET ability_type_id = 2 WHERE ability_type_id = 1;
UPDATE ability SET ability_type_id = 1 WHERE ability_type_id = 0;

CREATE TABLE ability_charakter_class
(id                INTEGER,
 ability_type_id   INTEGER,
 PRIMARY KEY(id),
 FOREIGN KEY (ability_type_id) REFERENCES ability_type(id) ON DELETE CASCADE
);


CREATE TABLE ability_charakter_class_text
(ability_charakter_class_id      INTEGER,
 language_id               INTEGER,
 name                            VARCHAR(64),
 description                     VARCHAR(4096),
 PRIMARY KEY(ability_charakter_class_id, language_id),
 FOREIGN KEY (ability_charakter_class_id) REFERENCES ability_charakter_class(id) ON DELETE CASCADE,
 FOREIGN KEY (language_id) REFERENCES language(id) ON DELETE CASCADE
);





CREATE TABLE charakter_class_ability
(charakter_class_id          INTEGER,
 ability_charakter_class_id  INTEGER,
 level                       INTEGER,
 PRIMARY KEY(charakter_class_id, ability_charakter_class_id),
 FOREIGN KEY (charakter_class_id) REFERENCES charakter_class(id) ON DELETE CASCADE,
 FOREIGN KEY (ability_charakter_class_id) REFERENCES ability_charakter_class(id) ON DELETE CASCADE
);



